import React from 'react';
import {apiUrl,filterData} from './data';
import Navbar from './components/Navbar';
import Filter from './components/Filter';
import Cards  from './components/Cards';
import {useEffect, useState} from 'react';
import { Spinner } from './components/Spinner';


export default function App(){




const [courses, setCourses] = useState(null);  // the initial value isn't important here

useEffect(()=>{

    const fetchdata = async ()=> {
        try{
            const response = await fetch(apiUrl);
            const output = await response.json();


            setCourses(output.data);   // there is main key 'data' containing all varieties
        }
        catch(error){
            toast.error("network mei koi dikkat hai");
        }
    }
    fetchdata();
},[]);

// function filterTopic(id){
//     console.log(id);
//     setCourses(courses.Development)

// }



    return(

        <div className="App">

            <Navbar/>

            <Filter filterData={filterData} />

            {courses ? (
             <Cards courses={courses} />           // it is important here because if the async function has not 
            ):(                                    // fetched data yet but we have rendered our page then we will get
                <p>Loading courses...</p>
            )}
        </div>
    )
}
