import React from 'react';
import {apiUrl,filterData} from './data';
import Navbar from './components/Navbar';
import Filter from './components/Filter';
import Cards  from './components/Cards';
import {useEffect, useState} from 'react';


export default function App(){




const [courses, setCourses] = useState(null);  // the initial value isn't important here

useEffect(()=>{

    const fetchdata = async ()=> {
        try{
            const res = await fetch(apiUrl);
            const output = await res.json();
            // sava data into a vairable

            setCourses(output.data);   // there is main key 'data' containing all varieties
        }
        catch(error){
            console.log("error");
        }
    }
    fetchdata();
},[]);

function filterTopic(id){
    console.log(id);

    const topic = courses.filterData[id-1].title;
    console.log(topic);

    setCourses(topic)

}



    return(

        <div className="App">

            <Navbar/>

            <Filter filterData={filterData} filterTopic={filterTopic}/>

            {courses ? (
             <Cards courses={courses} />           // it is important here because if the async function has not 
            ):(                                    // fetched data yet but we have rendered our page then we will get
                <p>Loading courses...</p>
            )}
        </div>
    )
}
