import Card from './Card';
import React from 'react';
export default  function Cards({courses}){

    // getting all data ie objects in one array
    // we have an object of arrays where each array consists of objects

    let allCourses =[];

    // returns an array of objects  //making an array of arrays
    const getCourses=()=>{       
        console.log(courses);
        Object.values(courses).forEach( (courseCategory)=>{
            courseCategory.forEach((course)=>{
                allCourses.push(course);
            })
        })
        return allCourses;


    }
    // console.log(getCourses());


    return(
        <div>
        {
            getCourses().map((course)=> {
                <Card course= {courses}/>

            })

        }
        </div>
    );
}
